        CREATE course:10 CONTENT {
            title: "Aerodynamics I",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "3131",
            req_group: 10187,
            description: "A study of the fundamental concepts of fluid mechanics as applied to aerodynamic applications with both differential and control volume analysis. Theory and application of viscous and inviscid incompressible flow including boundary layer theory and two dimensional airfoil theory. Prerequisites: A grade of C or better in each of the following: Aero Eng 2861; Math 1214 or Math 1211; Math 1215; Math 2222; Physics 1135 or Physics 1111.",
        };

               
        CREATE course:11 CONTENT {
            title: "Aerothermochemistry",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "4133",
            req_group: 10188,
            description: "Principles of thermochemistry in reacting flow including an introduction to fundamentals of quantum mechanics, statistical mechanics and statistical thermodynamics. Applications in flow through nozzles and shock waves, combustion, aerodynamic heating, ablation and propulsion. Prerequisites: Aero Eng 3131, Aero Eng 3171.",
        };

               
        CREATE course:12 CONTENT {
            title: "Aircraft&Space Veh Prop",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "4535",
            req_group: 10160,
            description: "Analysis of aircraft and missile propulsion systems; fundamentals of jet propulsion including air breathing and rocket engines. Introduction to advanced propulsion systems for space flights such as nuclear, thermonuclear, and plasma jets. Prerequisite: Mech Eng 3131, or Aero Eng 3171.",
        };

               
        CREATE course:13 CONTENT {
            title: "Aerospace Structures I",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "3251",
            req_group: 10153,
            description: "An introduction to various loads on aerospace vehicles. Basic theory and analysis of typical aerospace and related vehicle structures subjected to steady loading. Bending, shear, and torsion of open and closed section beams. Design of thin walled structures. Prerequisites: A grade of C or better in each of the following: Math 1214 (or 1211); Math 1215; Math 2222; Physics 1135 or Physics 1111; Civ Eng 2210.",
        };

               
        CREATE course:14 CONTENT {
            title: "Aerospace Structures II",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "4253",
            req_group: 10189,
            description: "Aircraft and spacecraft structure loads and regulations. Advanced methods in structural analysis using virtual work, energy methods, matrix methods, and finite element analysis. Thin plate theory and structural instability. Dynamic analysis of structures and fatigue analysis. Introduction to aeroelasticity. Prerequisite: Aero Eng 3251.",
        };

               
        CREATE course:15 CONTENT {
            title: "Flight Dynamics&Control",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "3361",
            req_group: 10190,
            description: "Static stability and control of conventional aircraft and implications in aircraft design. Six degrees of freedom time dependent equations of motion and their linearized solutions. Consideration of stability vs maneuverability, and the dynamic modes of motion of the aircraft. Prerequisites: Aero Eng 3613, Aero Eng 3131, and accompanied or preceded by Aero Eng 2780.",
        };

               
        CREATE course:16 CONTENT {
            title: "Aerodynamics II",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "3171",
            req_group: 10163,
            description: "Three dimensional incompressible wing theory. Compressible one dimensional flow with normal and oblique shock waves, heat addition, and friction. Compressible transonic, and supersonic linearized flow theory. Supersonic wings and wing/fuselage configurations. Prerequisite: C or better in Aero Eng 3131 and Mech Eng 2519.",
        };

               
        CREATE course:17 CONTENT {
            title: "Aerospace Systems Dsgn I",
            min_units: 2,
            max_units: 2,
            subject: "AERO ENG",
            catalog: "4780",
            req_group: 10191,
            description: "Consideration of the creative design process with emphasis on aeronautical-aerospace systems. Short design problems to illustrate the process. Selection of design projects for Aero Eng 4781. Information gathering for the design projects which will be completed in Aerospace Systems Design II. Fall semester. Prerequisites: Aero Eng 3251, 3361, 3171.",
        };

               
        CREATE course:18 CONTENT {
            title: "Aero Systems Design II",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "4781",
            req_group: 10192,
