        };

               
        CREATE course:32 CONTENT {
            title: "Smart Materials & Sensor",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "5229",
            req_group: 10207,
            description: "Smart structures with fiber reinforced polymer (FRP) composites and advanced sensors. Multi-disciplinary topics include characterization, performance, and fabrication of composite structures; fiber optic, resistance, and piezoelectric systems for strain sensing; and applications of smart composite structures. Laboratory and team activities involve manufacturing, measurement systems, instrumented structures, and performance tests on a large-scale smart composite bridge. Prerequisites: Senior standing and Math 3304. (Co-listed with Mech Eng 5229, Elec Eng 5270 and Civ Eng 5118)",
