        CREATE course:36 CONTENT {
            title: "Fracture Mechanics",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "5236",
            req_group: 10154,
            description: "Linear elastic and plastic mathematical models for stresses around cracks; concept of stress intensity; strain energy release rates; correlation of models with experiment; determination of plane stress and plane strain parameters; application to design. Prerequisite: Civ Eng 2210. (Co-listed with Mech Eng 5236)",
        };

               
        CREATE course:37 CONTENT {
            title: "Computational Fluid Dyn",
            min_units: 3,
            max_units: 3,
