        CREATE course:40 CONTENT {
            title: "Integrated Product Dev",
            min_units: 3,
            max_units: 3,
            subject: "AERO ENG",
            catalog: "5758",
            req_group: 10214,
            description: "Students in design teams will simulate the industrial concurrent engineering development process. Areas covered will be design, manufacturing, assembly, cost, and product support. Using a 3-D solid modeling program, students will design, analyze, and send the data base to the automated machine shop where the parts will be manufactured. The parts will then be assembled, tested and analyzed for their performance. Prerequisites: Aero Eng 3251 or Mech Eng 3708 for Design; Mech Eng 3313 for Assembly; Accompanied or preceded by Mech Eng 5653 for Manufacturing; Eng Mgt 5711 or 5714 for Cost/Product Support.",
        };

               
