        CREATE course:301031 CONTENT {
            title: "Environmental Monitoring",
            min_units: 3,
            max_units: 3,
            subject: "CHEM",
            catalog: "5710",
            req_group: 11035,
            description: "This course provides an overview of environmental monitoring methodologies. Discussion covers thermodynamic and kinetic processes that affect chemical transport and fate in the environment. Federal environmental regulations and remediation technologies are also covered with specific examples. Credit may not be given for both Chem 5710 and Chem 4710.  Prerequisites: Chem 2210, Physics 2111.",
        };

               
        CREATE course:301032 CONTENT {
