        CREATE course:301091 CONTENT {
            title: "Advanced Human Factors",
            min_units: 3,
            max_units: 3,
            subject: "PSYCH",
            catalog: "5710",
            req_group: NULL,
            description: "An in-depth review of the foundations of human factors, focusing on the interaction of people with various forms of technology in a variety of environments. Topics include research and evaluation methods, displays (e.g., visual, auditory), attention and information processing, decision making, motor skills, anthropometry, and biomechanics. (Co-listed with ENG MGT 5330)",
